import React from 'react';
import { Link } from 'react-router-dom';
import styles from './Products.module.css';
import { productsId } from '../../store/actions';
import { useDispatch } from 'react-redux';

export const Products = ({products=[],style={},amount,title}) => {
  const dispatch = useDispatch();
    // function onPlus(id){
    //   dispatch(productsId(id))
    // }
    const list = products.products.filter((_, i) => i < amount);
  return (
    <section className={styles.products} style={style}>
    {title && <h2>{title}</h2>}

    <div className={styles.list}>
      {list.map(({ id, images, title, category: { name: cat }, price }) => (
        <Link onClick={() => onPlus(id)} to={`/products/${id}`} key={id} className={styles.product}>
          <div
            className={styles.image}
            style={{ backgroundImage: `url(${images})` }}
          />

          <div className={styles.wrapper}>
            <h3 className={styles.title}>{title}</h3>
            <div className={styles.cat}>{cat}</div>
            <div className={styles.info}>
              <div className={styles.prices}>
                <div className={styles.price}>{price}$</div>
                <div className={styles.oldPrice}>
                  {Math.floor(price * 0.8)}$
                </div>
              </div>

              <div className={styles.purchases}>
                {Math.floor(Math.random() * 20 + 1)} purchased
              </div>
            </div>
          </div>
        </Link>
      ))}
    </div>
  </section>
  )
}
