const initCategories = { 
    categories: [],
    products:[]
}

const redusers = (state = initCategories, action) =>{
    switch (action.type) {
        case 'ADD_CATEGORIES' :
            return {...state.categories, categories:[ ...action.payload.value]};
            case 'GET_PRODUCTS' :
            return {...state.products, products:[...state, ...action.payload.value]}
        default:
            return state;
    }
}
export {
    redusers,
}
