import { user } from "../redusers";

export const fetchLesson = () =>{
    return async function ( dispatch ) {
        await fetch('https://api.escuelajs.co/api/v1/categories')
        .then(res => res.json())
        .then(elem => {
            let data = []
            elem.map((a)=> data.push(a.name))
            // const result = data.filter((item, index) => data.indexOf(item) === index);
            // console.log('>>>>',result)
            for(let i = 0; i < elem.length; i++){
                data.push(elem[i].name);
                result = data.filter((item, index) => data.indexOf(item) === index);

            }
            console.log(result)
            dispatch(user(elem))});
    }
}
