import { composeWithDevTools } from "redux-devtools-extension"; //расширения 
import {  categoriesRedux, productsRedux,} from './redusers';
import {cat, products} from './actions';
import thunk from 'redux-thunk'
import { legacy_createStore as createStore, combineReducers, applyMiddleware } from "redux";
const store = createStore(categoriesRedux);
store = createStore(productsRedux);
const bindActionCreator = (creator, dispatch) => (...args) =>{
    dispatch(cat(...args))
}
export const {dispatch, subscribe, getState} = store;
export const catDispatch = (payload) => dispatch(cat(payload));
export const prodDispatch = (payload) => dispatch(products(payload));

