import { composeWithDevTools } from "redux-devtools-extension"; //расширения 
import {  categoriesRedux,} from './redusers';
import {cat, products} from './actions';
import thunk from 'redux-thunk'
import { legacy_createStore as createStore, bindActionCreators, } from "redux";
const store = createStore(categoriesRedux);
export const {dispatch, subscribe, getState} = store;

const bindActionCreator = (creator, dispatch) => (...args) =>{
    dispatch(creator(...args));
}
export const catDispatch = bindActionCreator(cat, dispatch);
export const prodDispatch = bindActionCreator(products, dispatch)
