const initUser = [
    {
        id: 2,
        email: "maria@mail.com",
        password: "12345",
        name: "Maria",
        role: "customer",
        avatar: "https://picsum.photos/640/640?r=2849",
        creationAt: "2023-07-07T03:16:54.000Z",
        updatedAt: "2023-07-07T03:16:54.000Z"
        },
    ]

                const usersRedux = (state = initUser, action) =>{
                        switch (action.type) {
                            case 'LESSON1' :
                                return {...state, user:[...state.user, ...action.payload]};
                        default:
                            return state;
        }
    }

    export const user = (payload) => ({type: 'LESSON1', payload})

    export {
        usersRedux,
    }
