const initCategories = []

const redusers = (state = initCategories, action) =>{
    switch (action.type) {
        case 'ADD_CATEGORIES' :
            return {...state, categories:[...state, ...action.payload.value]};
            case 'GET_PRODUCTS' : 
            console.log(state)
            return {...state, products:[...state, ...action.payload.value]}
        default:
            return state;
    }
}
export {
    redusers,
}
