import React from 'react';
import {useSelector,useDispatch} from  'react-redux';

import Poster from '../Poster/Poster';
import { Products } from '../Products/Products';
import Categories from '../Categories/Categories';
import Banner from '../Banner/Banner';
import {fetchProductsData} from '../../store/asyncAction/asyncProducts'
import {filtered} from '../../store/actions'

export default function Home() {
    const dispatch = useDispatch();
    dispatch(fetchProductsData())
    // const data = useSelector((state) => state)
    React.useEffect(() => { 
      async function data () {
        const {categories, products} = useSelector((state) => state);
        const filteredData = (num) => products.products?.filter((item) => item.price < num);
        dispatch(filtered(await filteredData(10)))

      }
    }, [])
  return (
    <>
    <Poster />
    <Products products={products.products} amount={5} title={'Treding'} />
    <Categories products={categories.categories} amount={5} title={'Worth seeing'} />
    <Banner products={products.categories} amount={5} title={'Worth seeing'} />
    </>
  )
}
