import { composeWithDevTools } from "redux-devtools-extension"; //расширения 
import {categoriesRedux,productsRedux,filteredPrice} from './redusers';
import {cat, products} from './actions';
import thunk from 'redux-thunk'
import { legacy_createStore as createStore, combineReducers, applyMiddleware } from "redux";
const store = createStore();
export const {dispatch, subscribe, getState} = store;
const catDispatch = (payload) => dispatch(cat(payload))

const rootReduser = combineReducers({
    categories:categoriesRedux,
    products:productsRedux,
    // filtered:filteredPrice,
})

