export const cat = (value) => ({type: 'ADD_CATEGORIES', payload:{value}});
export const products = (value) => ({type: 'GET_PRODUCTS', payload:{value}});


const dispatch = useDispatch();

const categ = ( ) => {
    fetch('https://api.escuelajs.co/api/v1/categories')
   .then(res => res.json())
   .then(elem => dispatch(cat(elem)));
}

const prod = ( ) => {
  fetch('https://api.escuelajs.co/api/v1/products')
  .then(res => res.json())
  .then(elem1 => dispatch(products(elem1)));
}
