const initCategories = [
    {
        id: 2000,
        email: "maria@mail.com",
        password: "12345",
        name: "Maria",
        role: "customer",
        avatar: "https://picsum.photos/640/640?r=2849",
        creationAt: "2023-07-07T03:16:54.000Z",
        updatedAt: "2023-07-07T03:16:54.000Z"
        },
    ]

                const categoriesRedux = (state = initCategories, action) =>{
                        switch (action.type) {
                            case 'ADD_CATEGORIES' :
                                return {...state.categories, categories:[...state, ...action.payload]};
                        default:
                            return state;
        }
    }

    export const cat = (payload) => ({type: 'ADD_CATEGORIES', payload})

    export {
        categoriesRedux,
    }
