const categoriesSlice = {
    name: 'categories',
    initialState:{
        list: []
    },

}
const initState = {
    cash: 0
}

const cushReduser = (state = initState, action) =>{
    switch (action.type) {
        case 'ADD_NUMBER':
        return {...state, cash: state.cash + action.payload}
        case 'DELETE_NUMBER' : 
            return {...state, cash: state.cash + action.payload}

        default:
            return state;
    }
}

const customerReduser = (state = initState, action) =>{
    switch (action.type) {
        case 'ADD_CUSTOMER':
        return {...state, cash: state.cash + action.payload}
        case 'GET_CUSTOMERS' : 
            return {...state, cash: state.cash + action.payload}

        default:
            return state;
    }
}
export default {
    customerReduser,
    cushReduser
}
