import React from 'react';

import { AppRoutes } from '../Routes';
import Header from '../Header/Header';
import Sidebar from '../Sidebar/Sidebar';
import Footer from '../Footer/Footer';
import { useDispatch, useSelector } from 'react-redux';

export default function App() {
  const dispatch = useDispatch();
  const cash = useSelector( state => state.cash.cash);//так можно посмотреть стейт
  const customer = useSelector( state => state.customer.customer)

  const addCustomer = (name) => {
    const customer2 = {
      name,
      id: 1
    }
    dispatch({type:'ADD_CUSTOMER', payload: customer2})
  }
  const addCustomer1 = (name) =>{
    const add = {
      id: 22,
      name: name
    }
    dispatch({type: 'addCustom', payload: add})
  }
  const data = useSelector(state => state.customer.name);
  console.log(data)

  const removeCustomer = (customer) =>{
    dispatch({type:'DELETE_CUSTOMERS', payload: customer})
  }

  return (
    <div className='app'>
      <Header />
      {customer.length > 0 ? customer.map((a) => <div>{a.name}</div>)  : <div>NOT A CLIENTS</div>}
      <div className='container'> 
      <button onClick={()=> addCustomer1('Maga')}>GOOGOG</button>
      <button onClick={() => removeCustomer(1)}>NONOON</button>
      <Sidebar />
      <AppRoutes />
      </div>
      <Footer />
    </div>
  )
}
