import { legacy_createStore as createStore } from "redux";


const initState = {
    cash: '0',
}

const reduser = (state = initState, action) =>{
    switch (action.type) {
        case 'ADD_NUMBER':
        return {...state, cash: state.cash + action.payload}
        case 'DELETE_NUMBER' : 
            return {...state, cash: state.cash + action.payload}

        default:
            return state;
    }
}
export const store = createStore(reduser);
