const initGetProduct = {
    getProduct:[]
}

export const getProductData = (state = initGetProduct, action) => {
    switch(action.type){
        case 'GET_PRODUCT_DATA': 
            return {...state.getProduct, getProduct:[ action.payload.value ]}
            default: 
            return state
    }
}
export const categories = (state = initCategories, action) =>{
    switch (action.type) {
        case 'GET_CATEGORIES' :
            return {...state.categories, categories: [...action.payload.value]}
        default : 
            return state;
    }
}
